    func test_decode_serverListJson() throws {
        let sut = try JSONDecoder().decode(ServerListJson<RecommendationsListItem>.self, from: serverListData)
        XCTAssertEqual(sut.count, 5)
        let recommendation = sut.results.first!
        XCTAssertEqual(recommendation.uuid, "d4a8768b-8a8e-43e3-8c96-ed59d26df30d")
        XCTAssertEqual(recommendation.associationUuid, "4d6b8144-eec1-495e-a536-9fb559d2e406")
        XCTAssertEqual(recommendation.project?.association?.host?.fullName, "Anusha Nirmalananthan")
        XCTAssertNotNil(recommendation.project?.association?.title)
        XCTAssertEqual(recommendation.project?.association?.location?.company?.name, "Workfinder")
        XCTAssertEqual(recommendation.project?.association?.location?.company?.logo, "https://api-workfinder-com-develop.s3.amazonaws.com/media/companies/derived/eab0f2b5-d62c-45cf-91f0-d1ba37fd6d49.png")
    }
    
    func test_decode_recommendation() throws {
        let sut = try JSONDecoder().decode(RecommendationsListItem.self, from: recommendationItemData)
        XCTAssertNotNil(sut.project?.association?.title)
    }

